#overviews #slider-wrap .slide[data-num="1"] {
    display: block;
    background: url(http://7xtawy.com1.z0.glb.clouddn.com/blog/top/img01.jpg) center center no-repeat fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    background-size: cover
}

#overviews #slider-wrap .slide[data-num="2"] {
    background: url(http://7xtawy.com1.z0.glb.clouddn.com/blog/top/22.jpg) center center no-repeat fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    background-size: cover
}

#overviews #slider-wrap .slide[data-num="3"] {
