    content: "\f08d";
    float: left;
    margin-right: 10px
}

.article-pop-out:after {
    content: "";
    margin-right: 20px;
    margin-top: 9px;
    float: left
}

::-webkit-scrollbar {
    width: 10px;
    height: 10px
}

::-webkit-scrollbar-button {
    width: 0;
    height: 0
}

::-webkit-scrollbar-button:end:decrement,
::-webkit-scrollbar-button:start:increment {
    display: none
}

::-webkit-scrollbar-corner {
    display: block
}

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 8px;
    border-radius: 8px;
    background-color: rgba(0, 0, 0, .2)
}

::-webkit-scrollbar-thumb:hover {
    -webkit-border-radius: 8px;
    border-radius: 8px;
    background-color: rgba(0, 0, 0, .5)
}

::-webkit-scrollbar-thumb,
::-webkit-scrollbar-track {
    border-right: 1px solid transparent;
    border-left: 1px solid transparent
}

::-webkit-scrollbar-track:hover {
    background-color: rgba(0, 0, 0, .15)
}

::-webkit-scrollbar-button:start {
    width: 10px;
    height: 10px;
    background: url(../img/scrollbar_arrow.png) no-repeat
}

::-webkit-scrollbar-button:start:hover {
    background: url(../img/scrollbar_arrow.png) -15px 0 no-repeat
}

::-webkit-scrollbar-button:start:active {
    background: url(../img/scrollbar_arrow.png) -30px 0 no-repeat
}

::-webkit-scrollbar-button:end {
    width: 10px;
    height: 10px;
    background: url(../img/scrollbar_arrow.png) 0 -18px no-repeat
}

::-webkit-scrollbar-button:end:hover {
    background: url(../img/scrollbar_arrow.png) -15px -18px no-repeat
}

::-webkit-scrollbar-button:end:active {
    background: url(../img/scrollbar_arrow.png) -30px -18px no-repeat
}

#viewer {
    position: fixed;
    z-index: 1000000;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    overflow: hidden
}

#viewer-box {
    width: 100%;
    height: 100%;
    position: relative;
    color: #ccc;
    -webkit-transform: translate3d(-100%, 0, 0);
    -webkit-transition: -webkit-transform .25s ease-in-out
}

#viewer-box .viewer-box-l {
    background: rgba(20, 20, 20, .9);
    width: 80%;
    height: 100%;
    float: left
}

#viewer-box .viewer-box-l .viewer-box-wrap {
    margin: 20px 10px 0 20px
}

#viewer-box .viewer-box-l .viewer-title {
    line-height: 32px
}

#viewer-box .viewer-box-l .viewer-title:before {
    content: "";
    width: 6px;
    height: 6px;
    border: 1px solid #999;
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: #aaa;
    display: inline-block;
    margin-right: 10px
}

#viewer-box .viewer-box-l .viewer-div {
    border-bottom: 1px dotted #666;
    padding-bottom: 13px;
    line-height: 20px
}

#viewer-box .viewer-box-l .viewer-div:last-child {
    border-bottom: none
}

#viewer-box .viewer-box-l .viewer-div .switch-friends-link {
    line-height: 20px
}

#viewer-box .viewer-box-r {
    background: rgba(0, 0, 0, 0);
    width: 20%;
    height: 100%;
    float: right
}

#viewer-box.anm-swipe {
    -webkit-transform: translate3d(0, 0, 0)
}

#viewer-box.anm-swipe .viewer-box-r {
    background: rgba(0, 0, 0, 0)
}

.hide {
    display: none
}

#viewer-box .viewer-list {
    margin: 0;
    padding: 0;
    height: 100%;
    overflow: hidden
}

